def move_floor(char)
  values = { "(": 1, ")": -1 }
  values[char.to_sym] || 0
end

floor = input.chars.inject(0) do |acc, curr|
  acc + move_floor(curr)
end

puts floor

def find_floor(input, floor)
  current = 0
  input.chars.each_with_index do |char, index|
    current += move_floor(char)
    return index + 1 if current == floor
  end
end

puts "find_floor", find_floor(input, -1)
